513.source:
function checkPageNumbers() {
    for(var i = 0; i < this.pages.length; i++) {
        this.pages[i].pageNumber = i;
    }

}

519.source:
function newDefaultPage(pageNumber) {
    var morphsForPage = this.defaultPageMorphs.map(function(aMorph) {
        return aMorph.copy();
    })
    this.insertNewPageBefore(pageNumber, morphsForPage)
}

522.source:
function insertNewPageBefore(pageNumber, morphs) {
    this.sortPages();
    var page = {
        pageNumber: pageNumber,
        morphs:morphs
    }
   if (pageNumber < this.pages.length) {
       this.pages.pushAt(page, pageNumber)
   } else {
       this.pages.push(page)
   }
   this.checkPageNumbers()
   this.showPage(page)
}

525.source:
function copyAsPage(pageNumber) {
    var morphsForPage = this.pageMorphs().map(function(ea) {return ea.copy()})
    this.insertNewPageBefore(pageNumber, morphsForPage)
}

528.source:
function deletePage(pageNumber) {
    if (!this.validPageNumber(pageNumber)) return;
    this.pages.splice(pageNumber, 1)
    this.checkPageNumbers()
    this.showPageByNumber(this.currentPage.pageNumber)
}

531.source:
function pageMorphs() {
    var result = []
    this.submorphs.forEach(function(ea) {
        if (ea.name == 'CompositionBox') return;
        if (ea.name == 'PresentationBox') return;
        result.push(ea);
    })
    return result;
}

537.source:
function validPageNumber(pageNumber) {
    return pageNumber >=0 && pageNumber < this.pages.length;
}

540.source:
function morphMenuItems() {
    var items = $super();
    return items.concat([
        ["Composition Mode", this.compositionMode.bind(this)],
        ["Presentation Mode", this.presentationMode.bind(this)]
    ])

}

