function onClojureCaptureStateUpdate(captures, thenDo) {
  captures.forEach(function(ea) {
    if (ea.values) ea.values.map(function(v) {
      try { return eval(ea); } catch (e) { return ea; }
    })
  });

  var listItems = captures
    .groupBy(function(ea) { return ea.ns + "/" + ea.name; })
    .reduceGroups(function(defs, name, captures) {
      return defs.concat([{isListItem: true, string: name, value: captures}]);
    }, []).toArray();

  var defList = this.get("defList");
  defList.setListSilently(listItems, {selectByString: true});

  thenDo && thenDo();

  if (!this.onDefSelected()) return false;
  // if (!this.onHistSelected()) return false
  return true;
}

