function onHistSelected(_) {
  var defList = this.get("defList");
  var captureData = defList.selection;
  if (!captureData) return false;

  var histList = this.get("histList");
  var capt = histList.selection;
  if (!capt) {
    // this.get("workspaceEditor").hideTextOverlays();
    this.get("workspaceEditor").textString = "";
    this.get("traceEditor").setAttributedText([[""]]);
    return;
  }

  var accTemplate = "; access with:\n(rksm.cloxp-trace.capturing/inspect\n \"%s\"%s)\n\n; values\n%s\n";
  var content = lively.lang.string.format(accTemplate,
    capt.id, capt.hasOwnProperty("n") ? " "+capt.n : "",
    capt.values && capt.values.length ? capt.values.join("\n") : (capt.value || "nothing captured"));

  this.get("workspaceEditor").textString = content;
  // this.get("workspaceEditor").hideTextOverlays();

  var textAttrs = capt.traces ?
    Array.prototype.concat.apply([], capt.traces.invoke('slice', 0,5).map(Global.clojure.TraceFrontEnd.StackTrace.printFrames).delimWith(["\n\n"])) :
    Global.clojure.TraceFrontEnd.StackTrace.printFrames(capt.trace);

  this.get("traceEditor").setAttributedText(textAttrs)

  return true;
}

