function onDefSelected(n, o) {
  var captures = this.get("defList").selection,
      self = this;

  if (!captures) {
    this.get("histList").setList([]);
    this.onHistSelected();
    // this.get("workspaceEditor").hideTextOverlays();
    this.get("workspaceEditor").textString = "";
    this.get("traceEditor").setAttributedText([[""]]);
    return false;
  }

  var hist = captures.reduce(function(result, capture) {
    return result.concat([{isListItem: true, string: capture["id"], value: capture}]
      .concat(capture.times.map(function(time, i) {
        return {
          isListItem: true,
          string: lively.lang.date.format(new Date(time), "  HH:mm:ss.l") + " => " + String(capture.values[i]).truncate(20),
          value: {
            capture: capture,
            n: i, time: time,
            value: capture.values[i],
            trace: capture.traces[i],
            ns: capture.ns,
            id: capture.id
          }
        }
      })));
  }, []);


  ;(function() {
    if (!self.get("histList").selection) { self.showDefSource(); }
  }).delay(0);

  // ;(function() { if (!self.get("histList").selection) { self.showDefSource(); } }).delay(0);

  this.get("histList").setListSilently(hist, {selectByString: true})

  if (n && o && n != o) this.get("histList").deselectAll();


  return true;
}

