function showDefSource(thenDo) {
  var captures = this.get("defList").selection,
      self = this;
  if (!captures || !captures.length) return thenDo && thenDo();

  var ed = this.get("workspaceEditor");
  var qName = captures[0].ns +"/" + captures[0].name;
  Global.clojure.Runtime.doEval(
    lively.lang.string.format("(rksm.system-navigator.ns.internals/source-for-symbol '%s)", qName),
    {passError: true, prettyPrint: true, requiredNamespaces: ["rksm.system-navigator.ns.internals"]},
    function(err, source) {
      if (err) ed.setStatusMessage("Error retrieving source for " + qName + "\n" + String(err).truncate(500));
      else if (!self.get("histList").selection) {
        try { source = eval(source); } catch (e) {}
        ed.textString = source;
      }
      thenDo && thenDo(err);
    })
}

