function fetchCapturesAndUpdate(selectId, thenDo) {
  if (typeof selectId === "function") { thenDo = selectId; selectId = null; }

  var self = this;
  lively.lang.fun.composeAsync(
    function(n) { Global.clojure.TraceFrontEnd.retrieveCaptures({}, n); },
    function(captures,n) { self.onClojureCaptureStateUpdate(captures); n(); },
    function(n) { setTimeout(n, 100); },
    function(n) {
      if (!selectId) n(); else self.selectId(selectId, n); }
  )(thenDo);
}
