896.source:
function itemsFilter(filters, items) {
    filters = (filters || []).map(function(ea) {
        return ea.toLowerCase ? ea.toLowerCase() : ea; });
    var result = items.filter(function(item) {
        var s = item.string, descr = item.value.description || "";
        return filters.every(function(filter) {
            if (!filter || !filter.length) {
                return true;
            } else if (Object.isString(filter)) {
                return s.toLowerCase().include(filter)
                    || descr.toLowerCase().include(filter);
            } else if (Object.isRegExp(filter)) {
                return filter.test(item.string) || filter.test(descr);
            } else { return true; }
        });
    });
    return result;
}

