function showProject(p) {
  // var ed = this.get("projectDescription").aceEditor
  // m=ed.session.getMode()

  // ed.removeAllListeners("click")
  var descr = this.get("projectDescription");
  var self = this;
  // descr.aceEditor.session.setMode('ace/mode/text')
  if (!p) { descr.textString = ""; return; };
  descr.withAceDo(function(ed) {
      // delete descr.aceEditor.session.$modes["ace/mode/attributedtext"]
    // ed.session.setMode("ace/mode/attributedtext")
    // ed.session.setMode("ace/mode/text")
    // ed._eventRegistry.click

    // m.initMouseHandler
    // ed.session.
    // ed._eventRegistry.click

    var m = ed.session.getMode()
    var url = p.url || (p.scm && p.scm.url) || null;

    var spec = [
      // [p["group-id"]+"/"+p["artifact-id"]+"\n", "text"],
      [(p.description || "no description"), "text"],
      ["\n"],
      [url || "no url", {
        type: "markup.underline.link",
        onClick: function(evt, ed, attr) { window.open(url); }
      }],
      ["\n\nversions:\n"]
    ].concat(p.versions.reduce(function(attr, v) {
