function loadProjectList(thenDo) {
  return this.loadProjectListViaHTTP(thenDo);

  // -=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-

  var self = this;    

  var indicatorClose;
  lively.lang.fun.composeAsync(
      function(n) { self.withLoadingIndicatorDo("Fetching projects", function(err, indicator, close) { indicatorClose = close; n(); }); },
      fetchProjects,
      updateList
  )(function(err, projects) {
     indicatorClose && indicatorClose();
     if (err) {
       self.get("projectDescription").setStatusMessage(String(err))
     }
     thenDo && thenDo(err, projects);
  });

  function fetchProjects(n) {
    Global.clojure.Runtime.doEval(
      "(clojure.data.json/write-str (rksm.system-navigator.clojars/clojars-project-defs))",
      {requiredNamespaces: ["clojure.data.json", "rksm.system-navigator.clojars"], resultIsJSON: true, passError: true}, n);
  }

  function updateList(projects, n) {

    var items = projects.map(function(ea) {
      var name = (ea["group-id"] ? ea["group-id"]+"/" : "") + ea["artifact-id"];
      ea.name = name;
      return {
        isListItem: true,
        string: name,
        value: ea
      }
    }).reverse()
    self.get("projectList").setList(items);
    n(null, projects);
  }
}

