1001.source:
function installPackage(p, version, thenDo) {
  var self = this, indicatorClose, installResultEd,
      depVec;

  if (!p || !p.name)
    return thenDo && thenDo(new Error("Invalid package: " + p));

  // 1. Install package
  lively.lang.fun.composeAsync(
    function(n) { self.withLoadingIndicatorDo("Installing\n" + p.name, function(err, indicator, close) { indicatorClose = close; n(); }); },
    loadPackage,
    function(installResult, n) {
      findProjectConf(function(err, conf) { n(err, installResult, conf); })
    }
  )(function(err, result, conf) {
    indicatorClose && indicatorClose();
    showInstallResult(err, result, conf);

    if (err) return thenDo && thenDo(err);

    // 2. Try to add new package to project conf
    false && lively.lang.fun.composeAsync(
      findProjectConf,
      // askToAddDepToProjectConf,
      addDepToProjectConf
    )(function(err, result) {
      show(result)
      if (installResultEd && (err || result)) {
        installResultEd.setStatusMessage(
          err ? "Error adding project to configuration file:\n" + String(err) :
            "Successfully added project to configuration file", null, err ? null : 3);
      }
      thenDo && thenDo(err);
    })
  });

  // -=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-

  function loadPackage(thenDo) {
    // show("%s -- %s", p, version)
    var code = '(clojure.data.json/write-str (rksm.system-navigator.clojars/install-clojar-package-and-report-namespaces "%s" "%s" "%s"))';
    var bound = lively.lang.string.format(code, p["group-id"], p["artifact-id"], version);
    Global.clojure.Runtime.doEval(bound, {
      requiredNamespaces: ["rksm.system-navigator.clojars", "clojure.data.json"],
      resultIsJSON: true, passError: true}, thenDo);
  }

  function showInstallResult(err, result, confFile) {

    depVec = lively.lang.string.format('[%s/%s "%s"]',
                p["group-id"], p["artifact-id"], version);
    var msg;
    if (err) msg = [["Error installing:\n" + String(err).truncate(600)]];
    else {
      if (confFile) try { confFile = eval(confFile); } catch (e) {}
      msg = [
        ["Successfully installed " + depVec+"\n"],
        !confFile ? ["\n"] : ["Click here to add this dependency to\n" + confFile, {
          type: "meta.action.markup.underline.link",
          onClick: function(evt, ed, attr) {
            addDepToProjectConf(function(err) {
              installResultEd.setStatusMessage(
                err ? "Error adding " + depVec + " to " + confFile + ":\n" + String(err).truncate(600) :
                  depVec + " added to " + confFile)
            });
          }
        }],
        ["\n\n"],
        !result.namespaces || !result.namespaces.length ?
          [""] : ["Provided namespaces:\n  " + (result.namespaces || []).join("\n  ")],
          !result.namespaces || !result.namespaces.length ?
            [""] :
            ["\nClick here to load those namespaces and update browsers.", {
            type: "meta.action.markup.underline.link",
            onClick: function(evt, ed, attr) {
              loadNamespacesAndUpdateBrowsers(result.namespaces, function(err) {
                installResultEd.setStatusMessage(
                  err ? "Error loading namespaces:\n" + String(err).truncate(600) : "Namespaces loaded!")
              });
            }
          }],
        ["\n\n"],
        ["Please note: If there are errors loading this dependency please add\nit to your project configuration file and restart the server!"]]
    }

    installResultEd = $world.addActionText(
      msg, {title: "Installing " + p.name + " " + version, extent: pt(500,270)});
  }

  function findProjectConf(n) {
    var dir = lively.shell.cwd();
    Global.clojure.Runtime.doEval(
      lively.lang.string.format(
        '(rksm.cloxp-projects.core/find-project-configuration-file "%s")',
        dir),
      {requiredNamespaces: ["rksm.cloxp-projects.core"], passError: true},
      function(err, confFile) {
        n(null, err || !confFile || confFile.trim() === 'nil' ? null : confFile.trim());
      });
  }

  // function askToAddDepToProjectConf(confFile, n) {
  //   $world.confirm(
  //     lively.lang.string.format("Add %s to %s?", depVec, confFile),
  //     function(input) {
  //       n(null, input ? confFile : null)
  //     });
  // }

  function addDepToProjectConf(n) {
    var dir = lively.shell.cwd();
    Global.clojure.Runtime.doEval(
      lively.lang.string.format(
        '(rksm.cloxp-projects.core/add-dep-to-project-conf! "%s" \'%s \'%s "%s")',
        dir, p["group-id"], p["artifact-id"], version),
      {requiredNamespaces: ["rksm.cloxp-projects.core"], passError: true}, n);
  }

  function loadNamespacesAndUpdateBrowsers(namespaces, thenDo) {
    // show(namespaces);
    // var code = namespaces.map(function(ea) { return "(require '" + ea + " :reload)"; }).join("\n");
    var code = namespaces.map(function(ea) { return "(rksm.cloxp-repl/require-ns '" + ea + ")"; }).join("\n");
    show(code)
    lively.lang.fun.composeAsync(
      function(n) { Global.clojure.Runtime.doEval(code, {requiredNamespaces: ["rksm.cloxp-repl"], passError: true}, n); },
      function(_, n) {
        var b = $world.get("ClojureBrowser");
        if (!b) return n(null);
        b.getWindow().targetMorph.reload({}, n)
      }
    )(thenDo);

  }

}

