function loadProjectListViaHTTP(thenDo) {
  var self = this;    

  var indicatorClose;
  lively.lang.fun.composeAsync(
      function(n) { self.withLoadingIndicatorDo("Fetching projects", function(err, indicator, close) { indicatorClose = close; n(); }); },
      getProjectFileName,
      getJsonViaHTTP,
      updateList
  )(function(err, projects) {
     indicatorClose && indicatorClose();
     if (err) {
       self.get("projectDescription").setStatusMessage(String(err))
     }
     thenDo && thenDo(err, projects);
  });

  function getProjectFileName(n) {
    Global.clojure.Runtime.doEval(
      "(.getCanonicalPath (rksm.system-navigator.clojars/ensure-clojure-feed-in-a-file))",
      {requiredNamespaces: ["rksm.system-navigator.clojars"], resultIsJSON: false, passError: true}, n);
  }

  function getJsonViaHTTP(fileName, n) {
    // /home/lively/cloxp/LivelyKernel/2015-01-14_12-clojars-feed.json
    fileName = fileName.replace(/^"|"$/g, "");
    var url = Global.URL.nodejsBase.withFilename("CommandLineServer/read-file?mimeType=application/json&fileName=" + fileName)
    var webR = url.asWebResource().withJSONWhenDone(function(json, status) {
      if (!json || json.error || !status.isSuccess) {
        var reason = String(json ? json.error : status);
        n(new Error(reason));
      }
      n(null, json);
    }).beAsync().get();
  }

  function updateList(projects, n) {

    var items = projects.map(function(ea) {
      var name = (ea["group-id"] ? ea["group-id"]+"/" : "") + ea["artifact-id"];
      ea.name = name;
      return {
        isListItem: true,
        string: name,
        value: ea
      }
    }).reverse()
    self.get("projectList").setList(items);
    n(null, projects);
  }

}
