function withProjectConfigDo(mixin, cached, thenDo) {
  if (typeof cached === "function") {
    thenDo = cached;cached = false;
  }
  if (typeof mixin === "function") {
    thenDo = mixin;mixin = null;cached = false;
  }
  if (!mixin) mixin = {};

  var oldD = lively.lang.obj.merge(this.projectData(), mixin),
      pMgr = $morph("ProjectOverview").getWindow().targetMorph,
      self = this;

  lively.lang.fun.composeAsync(self.withProjectManagerDo.bind(self), function (pMgr, n) {
    pMgr.fetchProjectInfos([oldD], { cached: cached }, n);
  }, function (infos, n) {
    if (!infos[0]) return n.call(new Error("No data"));
    var d = infos[0];
    self._projectData = lively.lang.obj.merge(self._projectData, d, { name: d.name || d.cljName || oldD.name });
    n(null, self._projectData);
  })(thenDo);
}

