function refresh(thenDo) {
  var self = this,
      data;
  lively.lang.fun.composeAsync(this.withProjectConfigDo.bind(this, { dir: this.projectData().dir }, false), function (_data, n) {
    data = _data;
    if (data) {
      if (!data.name) data.name = data.dir.split(/\/|\\/).invoke("trim").compact().last();
      var st = data.gitStatus;
      var changed = st && st.hasLocalChanges;
      self.get("name").setRichTextMarkup([[data.name], [" "], [data.version || ""], [" "], [changed ? "(local changes)" : "", changed ? { fontWeight: "bold" } : {}]]);
    }
    n();
  }, self.warnIfDependentOutOfDate.bind(self))(function (err) {
    thenDo && thenDo(err, data);
  });
}

