function showDependentProjectsDo() {
  var self = this,
      config,
      editor;

  lively.lang.fun.composeAsync(createMarkup, show)(function (err) {
    err && self.setStatusMessage(String(err));
  });

  function createMarkup(n) {
    self.withDependentProjectsDo(function (err, deps, _config) {
      config = _config;
      var markup = [["Dependents of " + config.group + "/" + config.name + " " + config.version + ":\n\n"]].concat(lively.lang.arr.flatmap(deps, function (d) {
        return [[d.project], [" depends on "], [d.version], [d.version === config.version ? "" : " "], [d.version === config.version ? "" : "[update]", { type: "markup.underline.meta.action",
          onClick: function onClick() {
            lively.lang.fun.composeAsync(self.updateProjectDependencyVersion.bind(self, d, config, config.version), function (n) {
              d.version = config.version;n();
            }, createMarkup, function (markup, n) {
              editor.setAttributedText(markup);n();
            })(function (err) {
              if (err) self.setStatusMessage(String(err));
            });
          } }], ["\n"]];
      }));
      n(null, markup);
    });
  }

  function show(markup, n) {
    editor = $world.addActionText(markup, {
      title: "dependents of " + config.name
    });
    editor.getWindow().comeForward();
    n();
  }
}

