function withDependentProjectsDo(doFunc) {
  this.withProjectManagerAndConfigDo(true, function (err, c, pMgr) {
    var dependents = pMgr.allProjects().map(function (p) {
      if (!p) return;
      var dep = (p.dependencies || []).detect(function (dep) {
        return dep.name === c.name && dep.group === c.group;
      });
      if (!dep) return null;
      dep = lively.lang.obj.deepCopy(dep);
      dep.project = p.name;
      dep.projectGroup = p.group;
      dep.dir = p.dir;
      return dep;
    }).compact();

    doFunc(null, dependents, c);
  });
}

