function warnIfDependentOutOfDate(thenDo) {

  var self = this;

  this.withProjectManagerAndConfigDo(true, function (err, c, pMgr) {
    var all = pMgr.allProjects();

    // var deps = pMgr.dependenciesOfProject(c);
    var outOfDate = all.filter(function (p) {
      var dep = (c.dependencies || []).detect(pMgr.eqProject.curry(p));
      if (!dep) return false;
      if (!p.version.endsWith("-SNAPSHOT")) return p.version !== dep.version;
      return p.version.split("-")[0] !== dep.version.split("-")[0];
    });

    if (outOfDate.length) {
      self.get("warnButton").setVisible(true);
      self.get("warnButton").setToolTip("Dependencies out of date:\n" + outOfDate.pluck("name").join("\n"));
      self.warnings = function () {
        self.showDependencies();
      };
    } else {
      self.get("warnButton").setVisible(false);
      self.warnings = null;
    }

    thenDo && thenDo(err);
  });
}

