function showDependencies() {
  var self = this,
      config,
      editor;

  lively.lang.fun.composeAsync(self.withProjectManagerAndConfigDo.bind(self, true), createMarkup, show)(function (err) {
    err && self.setStatusMessage(String(err));
  });

  function createMarkup(project, pMgr, n) {
    config = project;

    var depProjects = pMgr.dependenciesOfProject(project);

    var markup = [["Dependencies of " + project.group + "/" + project.name + " " + project.version + ":\n\n"]].concat(lively.lang.arr.flatmap(depProjects, function (d, i) {

      var isOutOfDate = (function (project, dep) {
        if (!dep) return false;
        if (!project.version.endsWith("-SNAPSHOT")) return project.version !== dep.version;
        return project.version.split("-")[0] !== dep.version.split("-")[0];
      })(depProjects[i], d.dep);

      return [[d.name], [" @ "], [d.dep.version], [isOutOfDate ? "" : " out of date "], [isOutOfDate ? "" : "[update]", { type: "markup.underline.meta.action",
        onClick: function onClick() {
          lively.lang.fun.composeAsync(self.updateProjectDependencyVersion.bind(self, project, d, depProjects[i].version), function (n) {
            d.dep.version = depProjects[i].version;n();
          }, createMarkup.curry(project, pMgr), function (markup, n) {
            editor.setAttributedText(markup);n();
          })(function (err) {
            if (err) self.setStatusMessage(String(err));
          });
        } }], ["\n"]];
    }));
    n(null, markup);
  }

  function show(markup, n) {
    editor = $world.addActionText(markup, {
      title: "dependents of " + config.name
    });
    editor.getWindow().comeForward();
    n();
  }
}

