function interactivelyChooseConnection() {

  var self = this;

  lively.lang.fun.composeAsync(
    findConnections,
    createListItems,
    function(items, n) {
      lively.ide.tools.SelectionNarrowing.chooseOne(
        items,
        function(err, c) { self.connectTo(c); })
      n();
}
  )(function(err) {
    err && self.setStatusMessage(String(err).truncate(500), Global.Color.red);
  })

  // -=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-

  function findConnections(n) {
    var code = 
      "(clojure.data.json/write-str\n"
      + " (map\n"
      + "  (fn [c]\n"
      + "    (-> c\n"
      + "      (update-in [:server] :id)\n"
      + "      (dissoc :channel)))\n"
      + "  (rksm.cloxp-com.server/all-connections)))\n";
    var opts = {
      resultIsJSON:true, passError: true,
      requiredNamespaces: ["clojure.data.json", "rksm.cloxp-com.server"]
    }

    Global.clojure.Runtime.doEval(code, opts, n);
  }

  function createListItems(connections, n) {
    var items = [{isListItem: true, string: "disonnect", value: null}]
      .concat(connections.map(function(ea) {
        return {
          isListItem: true,
          string: self.printConnection(ea),
          value: ea
        }
      }));
    n(null, items);
  }

}

