function onKeyDown(evt) {
  var keys         = evt.getKeyString(),
      handled      = false;

  switch (keys) {
    case "Alt-T": this.interactivelyChooseConnection(); handled = true; break;
  }

  if (!handled) return $super(evt);
  evt.stop(); return true;
}

