function itemsFilter(filters, items) {
    filters = (filters || []).map(function(ea) {
        return ea.toLowerCase ? ea.toLowerCase() : ea; });

    var result = items.filter(function(item) {
      var name = item.value.name || item.string || "";
      // var name = item.string || "";
      return filters.all(function(filter) {
          if (!filter || !filter.length) {
              return true;
          } else if (Object.isString(filter)) {
              return name.toLowerCase().include(filter);
          } else if (Object.isRegExp(filter)) {
              return filter.test(name);
          } else { return true; }
      });
    });

    // add parents

// var i = this.get("list").getSelectedItem()
// var items = this.get("list").itemList
// result = [i]
// itemWithParents(i, [])

// var parent = items.detect(function(ea) {
//   return ea.value["call-id"] === i.value["parent-frame"]; });


    result = result.reduce(function(resultItems, item) {
      return resultItems.concat(itemWithParents(item, resultItems));
    }, []);

    return result;

    // -=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-

    function itemWithParents(item, includedItems) {
        var frame = item && item.value;
        if (frame && includedItems.include(item)) return [];
        if (!frame || frame.depth < 1 || !frame["parent-frame"])
          return [item];
        var parent = items.detect(function(ea) {
          return ea.value["call-id"] === frame["parent-frame"]; });
        return itemWithParents(parent, includedItems).concat([item]);
    }
}

