function showDetails() {
  var l = this.get("FilterableList").get("list");
  var frame = l.selection;
  if (!frame) return $world.inform("Nothing selected");
  var allFrames = l.getValues();;

  var parents = this.getParentFrames(frame, allFrames);
  var content = lively.lang.string.format("%s\n\narguments: %s\n\n=> %s\n\n%s",
    frame.name,
    frame['printed-args'] || "",
    frame.result,
    parents.length ?
      "Called from:\n" + parents.map(function(ea) {
        return lively.lang.string.indent(
          ea.name + " [" + (ea['printed-args']||"") + "]",
          " ", ea.depth-1);
      }).join("\n") :
    "");

  $world.addCodeEditor({
    title: "Details for invocation of " + frame.name,
    content: content,
    lineWrapping: true,
    textMode: "text"
  }).getWindow().comeForward();
}

