function showTimings() {

  var self = this;
  var sortKey = "time";
  var l = this.get("FilterableList").get("list");
  var allFrames = l.getValues();

  lively.lang.fun.composeAsync(
    askForSortKey,
    createTimings.curry(allFrames),
    createTable,
    showTimings
  )(function(err) { err && show(err); })

  function askForSortKey(n) {
    lively.morphic.Menu.openAtHand("Sort by", ["time", "calls", "avgTime"].map(function(k) {
      return [k, function() { sortKey = k; n(); }];
    }));
  }

  function createTimings(frames, n) {
    var timings = frames.reduce(function(timings, frame) {
      var t = timings[frame.name] || (timings[frame.name]  = {name: frame.name, calls: 0, time: 0, avgTime: 0});
      t.calls++;
      t.time += frame.time;
      t.avgTime = t.time/t.calls;
      return timings;
    }, {});
    n(null, timings);
  }

  function createTable(timings, n) {
    var vals = lively.lang.obj.values(timings).sortBy(function(ea) { return ea[sortKey]; }).reverse();
    var grid = lively.lang.grid.tableFromObjects(vals, 0);
    n(null, grid);
  }

  function showTimings(grid, n) {
    $world.addCodeEditor({
      title: "Timings for " + self.getTracedCode().truncate(50),
      content: lively.lang.string.printTable(grid),
      lineWrapping: false,
      textMode: "text",
      extent: pt(720, 450)
    }).getWindow().comeForward();
  }

}

