  if (!frame) return [];
  if (frame.depth <= 1 || !frame["parent-frame"]) return [frame];

  var parent = allFrames.detect(function(ea) {
    return ea["call-id"] === frame["parent-frame"]; });

  return this.getParentFrames(parent, allFrames).concat([frame]);
}

