    return lively.lang.string.format("%s%s [%s]\n%s => %s",
      i, f.name,
      f['printed-args'],
      i, f.result);
  }


  $world.addCodeEditor({
    title: "Raw trace",
    content: allFrames.map(printFrame).join("\n"),
    lineWrapping: false,
    extent: pt(800, 600),
    textMode: "text"
