function renderMd(mdSource) {
  var self = this;
  var r = new Global.marked.Renderer()
  r.codespan= function (text) {
    if (text.startsWith("doit")) return self.mdRenderDoit(text);
    if (text.startsWith("hover")) return self.mdRenderHover(text);
    if (text.startsWith("link")) return self.mdRenderLink(text);
    else return '<code>' + text + '</code>';
  }
  r.link = r.link.wrap(function(proceed) {
    var args = lively.lang.arr.from(arguments);
    args.shift();
    var html = proceed.apply(r, args);
    return html.replace("<a href", "<a target=\"_blank\" href");
  })
  Global.marked.setOptions({
    renderer: r,
    // gfm: true,
    tables: true,
    // breaks: false,
    // pedantic: false,
    // sanitize: true,
    // smartLists: true,
    // smartypants: false
  });
  // show(Global.marked(this.mdSource()))
  this.get("markdownMorph").setHTML(
    '<div class="' + this.state.page.split("$").concat("page").join(" ") + '">'+
    // '<div class="page">'+
      Global.marked(mdSource)
    +'</div>'
    );
}

