function showMdEditor(evt) {
  var editor = $world.addCodeEditor({
    title: "edit lively md",
    content: "",
    extent: this.getExtent(),
    textMode: "markdown"
  })
  editor.targetId = this.id;
  editor.addScript(function update() {
    this.textString = this.getMorphById(this.targetId).mdSource(), null, 2;
  });
  editor.addScript(function doSave() {
    this.savedTextString = this.textString;
    lively.lang.fun.debounceNamed(this.id+"mdSave", 200, function() {
      var t = this.getMorphById(this.targetId);
      if (!t) show("cannot find target md morph!")
      else t.onNewMd(this.textString);
    }.bind(this))()
  })
  lively.bindings.connect(editor, 'textChange', editor, 'doSave');
  editor.update();
}

