function showPage(group, number, noHist) {
  if (number === undefined && typeof group === "string") {
    var groupAndN = group.split("$");
    group = groupAndN[0];
    number = Number(groupAndN[1])||0;
  }

  !noHist && this.state.history.push(this.state.page);
  this.state.page = group+"$"+number;
  this.render();
  this.onStateChange(this.state);
}

