function mdRenderHover(text) {
  var match = lively.lang.string.unescapeCharacterEntities(text).match(/hover(-image|-video)?(:?"([^"]*)")(.*)/);
  if (!match) return "unable to read hover";
  var inner = match[3], rest = match[4];
