function showHoverImage(hoveredEl, url, extent) {
  if (typeof url === "string" && !url.startsWith("http"))
    url = Global.URL.root.withFilename(url);
  var self = this;
  var defaultExtent = pt(200,100);
  var opts = {};
  opts.useNativeExtent = extent ? false : true;
  // var img = this._hoverImage;
  // if (img) img.setImageURL(url, opts, whenLoaded);
  var img = new lively.morphic.Image(rect(0,0,100,100), url, opts, whenLoaded)

  // delete this._hoverImage
  // $world.submorphs.grep(/\.image/i).last().remove()
  extent && img.setExtent(extent);
  img.applyStyle({
    borderWidth: 1,
    borderColor: Global.Color.gray
  })

  align(img.openInWorld());

  function align(img) {
    var s = $world.getScroll();
    var elBounds = lively.Rectangle.fromElement(hoveredEl).translatedBy(pt(s[0], s[1]));
    img.align(img.bounds().topCenter(), elBounds.bottomCenter())
  }

  function whenLoaded(err, img) {
    if (img.getExtent().x < 20 || img.getExtent().y < 20)
      img.setExtent(defaultExtent);
    img.openInWorld();
    self.showMorphWhileHover(hoveredEl, img);
    align(img);
  }
}

