function videoMorph(videoName, extent, thenDo) {
  // this.videoMorph("cloxp/videos/sys-browser-narrow", function(err, v) { v.openInWindow(); v.play(); })
// $world.submorphs.grep(/wrapper/i).last().remove()
  // var videoName = "cloxp/videos/sys-browser-narrow"
  // var thenDo = function() {};

// lively.lang.arr.from(m.renderContext().shapeNode.childNodes).detect(function(ea) { return ea.tagName === 'video'; })
  if ((typeof extent === "function" || !extent) && !thenDo) {
    thenDo = extent; extent = null;
  }

  var useNativeExtent = !extent;
  if (!extent) extent = (pt(300,200));

  var m = new lively.morphic.HtmlWrapperMorph(extent);
  var html = lively.lang.string.format(
   "<video style=\"display: inline-block;\" width=\"%spx\" height=\"%spx\" autobuffer=\"autobuffer\">"
+   "<source src=\"%s.mp4\" type=\"video/mp4; codecs=&quot;avc1.42E01E, mp4a.40.2&quot;\"/>"
+   "<source src=\"%s.oggtheora.ogv\" type=\"video/ogg; codecs=&quot;theora, vorbis&quot;\"/>"
+ "</video>", extent.x, extent.y, videoName, videoName);
  m.setHTML(html);

  m.addScript(function videoElement() { return this.renderContext().shapeNode.childNodes[0]; });
  m.addScript(function setControlsVisible(bool) { return this.videoElement().controls = bool; });
  m.addScript(function play() { return this.videoElement().play(); });
  m.addScript(function pause() { return this.videoElement().pause(); });
  lively.lang.fun.waitFor(1000,
    function() { return m.videoElement() && m.videoElement().videoWidth != 0; },
    onload)

  function onload(err) {
    var n = m.videoElement();
    if (!err && useNativeExtent && n.videoWidth > 0) {
      m.setExtent(pt(n.videoWidth, n.videoHeight));
      n.width = n.videoWidth; n.height = n.videoHeight;
    }
    thenDo && thenDo(null, m);
  }
}

