function mdRenderLink(text) {
  var match = lively.lang.string.unescapeCharacterEntities(text).match(/link(:?"([^"]*)")(.*)/);
  if (!match) return "unable to read link";
    // <span class="back">`doit:"back" this.back(); `</span>
    // <span style="visibility: hidden;" class="next">`doit:"next"`</span>

  var text = match[2], link = match[3];
  if (link.trim() == "back") link = 'this.back();'
  else if (link.trim() == "next") link = 'this.showNext();'
  else link = "this.showPage("+link+");"
  return lively.lang.string.format('<a style="popsition: relative;" class="link" href="javascript:void(0);" onclick="(function() {\n %s\n}).call(lively.morphic.World.current().getMorphById(\'%s\'));\">%s</a>',
      link.replace(/"/g, '\\"'), this.id, text);
}
