function update() {
// this.startStepping(2000, "update")
  var d = lively.shell.cwd();
  var dirText = this.get("dir");
  var prev = dirText.textString;
  var nChars = dirText.getExtent().x*.016*dirText.getFontSize();
  var shortDir = d.length > 50 ? "..." + d.slice(-nChars) : d;
  dirText.textString = d;

  // FIXME clojure specific
  if (prev !== shortDir && module("clojure.Runtime").isLoaded()) {
    var code = lively.lang.string.format("(rksm.system-navigator.ns.filemapping/maybe-add-classpath-dir \"%s\")", d);
    Global.clojure.Runtime.doEval(code, {passError: true, requiredModules: ["rksm.system-navigator.ns.filemapping"]}, function(err, result) {
      if (err) show(err);
    });
  }
}

