function showSelectionMenu() {
  var self = this, pl = this.get("projectList");

  var allIndexes = lively.lang.arr.range(0, pl.getList().length-1);
  var items = [
    ["select all", function() {
      pl.isInLayoutCycle = true;
      try { pl.selectAllAt(allIndexes); } finally { pl.isInLayoutCycle = false; }
    }],
    ["select none", function() {
      pl.isInLayoutCycle = true;
      try { allIndexes.forEach(pl.deselectAt.bind(pl)); } finally { pl.isInLayoutCycle = false; }
    }],
    ["invert selection", function() {
      pl.isInLayoutCycle = true;
      try {
        var indexes = allIndexes.withoutAll(pl.getSelectedIndexes());
        pl.getSelectedIndexes().forEach(pl.deselectAt.bind(pl));
        pl.selectAllAt(indexes);
     } finally { pl.isInLayoutCycle = false; }
    }]
  ]

  lively.morphic.Menu.openAtHand(null, items);
}

