function project_openConfFile(proj) {
  var self = this;

  lively.lang.fun.composeAsync(
    findCljConfFile,
    findPackageJson,
    openFile
  )(function(err) {
    if (err)
      self.setStatusMessage("Error retrieving project config:\n" + err);
  });

  // -=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-

  function findCljConfFile(n) {
    Global.clojure.Runtime.doEval(
      lively.lang.string.format(
        "(rksm.cloxp-projects.core/find-project-configuration-file \"%s\")", proj.dir),
      {requiredNamespaces: ["rksm.cloxp-projects.core"], passError: true},
      function(err, result) {
        if (err) n(err);
        else if (!result || result === "nil") n(null,null);
        else n(null, result.replace(/^"|"$/g, ""));
      });
  }

  function findPackageJson(projFile, n) {
    if (projFile) return n(null, projFile);
    lively.shell.run('echo "`pwd`/`ls package.json`";', {cwd: proj.dir}, function(err, cmd) {
      var out = cmd.resultString(true);
      if (err || !out || out.include("No such file or directory")) n(null);
      else n(null, out.trim());
    });
  }

  function openFile(file, n) {
    lively.ide.openFile(file);
    n();
  }
}

