function project_loadProject(proj, thenDo) {

  var indicatorClose, self = this;
  lively.morphic.tools.LoadingIndicator.open(
    "loading project\n" + proj.name,
    function(close) { indicatorClose = close; });

  setTimeout(function() { indicatorClose && indicatorClose(); }, 2*60*1000);

  lively.lang.fun.composeAsync(
    function(next) {
      var actions = lively.lang.fun.either(
        function() { next(new Error('timeout loading ' + proj.name)); },
        function(err, loadResult, warnings) { next(err, loadResult, warnings); });
      setTimeout(actions[0], 50*1000);
      load(actions[1]);
    }
  )(function(err, loadResult, warnings) {
    indicatorClose && indicatorClose();
    (function() {
      if (err || warnings.length) {
        $world.addCodeEditor({
          title: lively.lang.string.format("%s loading %s",
            err ? "error " : "warnings",
            loadResult && loadResult.dir ? " " + loadResult.dir : ""),
          content: lively.lang.string.format("%s\n\n\n%s",
            err ? String(err) + "\n\n\n" : "",
            warnings ? warnings.join("\n\n") : ""),
          textMode: "text",
          extent: pt(600,400),
          lineWrapping: false
        });
      } else self.setStatusMessage(loadResult.dir + " loaded");
      thenDo && thenDo(err, loadResult);
    }).delay(0.2);
  });

  function load(next) {
    var warnings = [];
    Global.clojure.Projects.loadProjectInteractively({
      showWarningFn: function(w) { warnings.push(w); },
      projectDir: proj.dir,
      askToLoadNamespaces: true,
      setCurrentDir: true,
      informBrowsers: true
    }, function(err, loadResult) { next(err, loadResult, warnings); });
  }
}

