function fetchProjectInfos(projects, opts, thenDo) {
  // var projects = this.defaultProjects();

  opts = opts || {};
  if (opts.cached) return thenDo(null, projects);

  projects = projects.map(manualDeps);

  var self = this;
  var grouped = projects.groupByKey("type");

  lively.lang.fun.composeAsync(

    function(n) {
      lively.lang.arr.mapAsyncSeries(grouped.js || [], function(p, _, next) {
        lively.lang.fun.composeAsync(getPackageJSON, packageJSONToProjectInfo)(p, next);
      }, function(err, projects) {
        if (err) show("fetchProjectInfos error: %s", err);
        n(err);
      });
    },

    function(n) { cljProjectInfos((grouped["undefined"] || []).concat(grouped.clj || []), n); },
    function(_, n) { self.projects_gitStatus(projects, {getRemoteStatus: false}, n); },
    function(_, n) { n(); }

  )(function(err) {
    err && self.setStatusMessage(String(err));
    thenDo(err, projects);
  });

  // -=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-

  function getPackageJSON(p, thenDo) {
    lively.shell.cat(p.dir + "/package.json", {}, function(err, source) {
      if (!err) try { var json = JSON.parse(source); } catch (e) {}
      thenDo(null, p, json || {});
    });
  }

  function packageJSONToProjectInfo(p, packageJson, thenDo) {
    if (!packageJson) return thenDo(null, p);
    if (!p.name) p.name = packageJson.name;
    var deps = uniqDeps((p.dependencies || [])
      .concat(packageJson.dependencies || [])
      .concat(packageJson.devDependencies || [])
      .concat(packageJson.optionalDependencies || []));
    lively.lang.obj.extend(p,
      {version: packageJson.version, dependencies: deps});
    thenDo(null, p);
  }

  function manualDeps(p) {
    p.dependencies = [].concat(p.deps || []);
    return p;
  }

  function cljProjectInfos(projects, thenDo) {
    var dirs = "[" + projects.map(function(p) { return '"' + p.dir + '"'; }).join(" ") + "]";
    var code = "(rksm.cloxp-projects.core/project-infos->json " + dirs + " {:include-plugins? true, :clean? true, :include-dev? true})";

    Global.clojure.Runtime.doEval(
      code,
      {requiredNamespaces: ["rksm.cloxp-projects.core"], resultIsJSON: true, passError: true},
      function(err, result) {
        if (err || !result) return thenDo(err, projects);
        projects = projects.map(function(p, i) {
          if (!result[i]) return p;
          var deps = (result[i].dependencies || []).map(function(ea) {
            var pair = ea[0].split("/");
            return {name: pair.length === 1 ? pair[0] : pair.last(), group: pair.first(), version: ea[1]};
          });
          if (!p.name) p.name = result[i].name;
          if (!p.name) p.name = result[i].dir.split(/\/|\\/).last();
          return lively.lang.obj.extend(p, {
            group: result[i].group,
            description: result[i].description,
            version: result[i].version,
            cljName: result[i].name,
            dependencies: uniqDeps((p.dependencies || []).concat(deps))
          });
        });

        thenDo(null, projects);
      });
  }

  function uniqDeps(deps) {
    return deps.uniqBy(function(a,b) {
      return (a.name || a) === (b.name || b); });
  }
}

