        })
      } else {
        projects.forEach(function(p) { deps[p.name] = (p.dependencies || []).clone(); });
      }
      n(null, projects, deps)
    },
    function(projects, deps, n) {
      var sorted = sortedProjectsByDeps([], projects, deps);
      n(null, sorted);
    }
  )(projects.clone(), thenDo);

  // -=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-

  function sortedProjectsByDeps(sorted, projects, deps, i) {
    if (!i) i = 0;
    if (!projects.length || i > 1000) return sorted.concat(projects).uniq();
    sorted = sorted.concat(projects.filter(function(ea) {
      return deps[ea.name].every(function(dep) {
        var name = Object.isString(dep) ? dep : dep.name;
        return sorted.some(function(ea) { return ea.name === name; })
      });
    }));
    projects = projects.withoutAll(sorted);
    return sortedProjectsByDeps(sorted, projects, deps, i+1);
  }

}

