function projects_gitStatus(projects, opts, thenDo) {
  opts = opts || {};
  var getRemoteStatus = !!opts.getRemoteStatus;

  var self = this;
  fetchStatus(projects, thenDo);

  // -=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-

  function fetchStatus(projects, thenDo) {
    // var pBar = $world.addProgressBar(null, "git status")
    var counter = 0;
    lively.lang.arr.mapAsyncSeries(projects,
      function(p, i, next) {
        lively.lang.fun.waitForAll([
          getRemoteStatus ?
            isRepoUpToDate.curry(p.name, p.dir, p.branch) :
            function(n) { n(null, false); },
          isRepoDirty.curry(p.name, p.dir, p.branch)],
          function(err, result) {
            // pBar.setValue(i/projects.length);
            p.gitStatus = {remoteHasUpdates: result[0][0], hasLocalChanges: result[1][0]}
            next(err, [p.name || p.dir.split(/\/|\\/).last(), result[0], result[1]]); });
      }, function(err, results) {
        // pBar.remove();
        thenDo && thenDo(err, results);
      });
  }

  function isRepoUpToDate(repoName, repoDir, branch, thenDo) {
    getRemoteAndLocalHeadRef(repoDir, branch, function(err, refs) {
      if (err) return thenDo && thenDo(err, false);
      thenDo && thenDo(null, refs.remote === refs.local);
    });
  }

  function isRepoDirty(repoName, repoDir, branch, thenDo) {
    var cmdString = 'cd ' + repoDir + '; [[ $(git diff --shortstat 2> /dev/null | tail -n1) != "" ]] && echo "*"';
    lively.shell.run(cmdString, function(cmd) {
      var isDirty = cmd.resultString(true).trim().length > 0;
      thenDo(null, isDirty); });
  }

  function getRemoteAndLocalHeadRef(repoDir, branch, thenDo) {
    branch = branch || "master";
    var cmdString = "if [[ ! -d " + repoDir + " ]]; then echo 'does not exist'; else "
                    + "cd " + repoDir + "; remote=`git ls-remote origin " + branch + "`;\n"
                    + "local=`git show-ref --hash " + branch + " | tail -n 1`;\n"
                    + "echo \"{\\\"remote\\\": \\\"$remote\\\", \\\"local\\\": \\\"$local\\\"}\"; fi;";
    lively.shell.run(cmdString, {}, function(cmd) {
      if (cmd.getStdout().match(/does not exist/)) return thenDo(null, {remote: "", local: repoDir + " does not exist"});
      var out;
      try {out = JSON.parse(cmd.getStdout().replace(/\s/g, " "))} catch (e) {
        show("cd " + repoDir + "\n" + cmdString + "\n" + cmd.resultString(true))
        return thenDo && thenDo(e); }
      var remoteRef = (out.remote || "").trim().split(" ").first(),
          localRef = (out.local || "").trim().split(" ").first(),
          result = {remote: remoteRef, local: localRef};
      thenDo && thenDo(null, result)})
  }
}
