function updateProjectDependencyVersion(project, dep, newVersion, thenDo) {
  var self = this, newVersion,
      depName = dep.name || dep;

  lively.lang.fun.composeAsync(
    function updateDep(n) {
      var code = lively.lang.string.format(
        '(rksm.cloxp-projects.versions/update-project-dep-in-file! [%s "%s"] "%s")',
        (dep.group ? dep.group + "/" : "") + depName, newVersion, project.dir);

      Global.clojure.Runtime.doEval(
        code,
        {requiredNamespaces: ["rksm.cloxp-projects.versions"], passError: true},
        function(err) { n(err); });
    },

    this.withProjectManagerDo.bind(this),
    function(pMgr, n) {
      var morph = pMgr.projectMorphFor(project.project || project.name, project.projectGroup || project.group);
      if (morph) morph.refresh(function() { n(); });
      else n();
    }
  )(function(err) { thenDo && thenDo(err); })
}

