function projects_showInfo(projects, fetchInfos, onlyLocal, shouldDoGitStatus, editorMorph, thenDo) {
  if (typeof onlyLocal === "undefined") onlyLocal = false;
  // var fetchInfos = !editorMorph;

  // ["show status", function() { self.projects_showStatusOverview(projects); }],
  // ["dependencies...", [
  //   ["local", function() { self.projects_printDependencies(projects, true, null); }],
  //   ["all", function() { self.projects_printDependencies(projects, false, null); }]
  // ]]

  var self = this,
      indicatorClose;

  var actionStyle = "meta.action.markup.underline";
  var localDepStyle = "markup.italic";
  var localDepActionStyle = "meta.action.markup.underline.markup.italic";

  lively.lang.fun.composeAsync(function step1_fetchProjectInfos(n) {
    if (!fetchInfos) n(null, projects);else {
      lively.lang.fun.composeAsync(function (n) {
        lively.require("lively.morphic.tools.LoadingIndicator").toRun(function () {
          lively.morphic.tools.LoadingIndicator.open("fetching data", function (close) {
            indicatorClose = close;n();
          });
        });
      }, self.fetchProjectInfos.bind(self, projects.clone(), { cache: true }))(n);
    }
  }, function step2_fetchProjectGitStatus(projects, n) {
    if (shouldDoGitStatus) self.projects_gitStatus(projects, { getRemoteStatus: true }, function (err) {
      n(err, projects);
    });else n(null, projects);
  }, function step3_printDependencies(projects, n) {
    printDependencies(projects, n);
  }, function (printed, next) {
    printed = [["[refresh]", { type: actionStyle, onClick: function onClick(evt, ed, attr) {
        update(true, true, ed.$morph);
      } }], ["\n\n"]].concat(printed);

    if (editorMorph) {
      editorMorph.setAttributedText(printed);
      editorMorph.getWindow().comeForward();
    } else {
      $world.addActionText(printed, {
        title: "Project info / status",
        extent: pt(480, 700),
        allowInput: false
      }).getWindow().comeForward();
    }

    next(null, printed);
  })(function (err, printed) {
    indicatorClose && indicatorClose();
    err && self.setStatusMessage(String(err));
    thenDo && thenDo(err, printed);
  });

  function printGitStatus(project) {
    var s = project.gitStatus;
    if (!s) return [];
    return [[lively.lang.string.format("local changes: %s, up-to-date: %s", s.hasLocalChanges, s.remoteHasUpdates)], [" "], ["[status]", { type: "action", onClick: function onClick() {
        self.project_runShellCommand("git st", project);
      } }], [" "], ["[diff]", { type: "action", onClick: function onClick() {
        self.project_runShellCommand("git diff", project);
      } }], ["\n"]];
  }

  function printDependencies(projects, thenDo) {
    var printedDeps = projects.reduce(function (printed, p) {
      var deps = (p.dependencies || []).clone();
      var localDeps = deps.filter(function (dep) {
        var n = dep.name || dep;
        return !!projects.detect(function (p) {
          return p.name === n;
        });
      });

      if (onlyLocal) deps = localDeps;else deps = localDeps.concat(deps.withoutAll(localDeps)); // reorder

      var depsPrinted = lively.lang.arr.flatmap(deps, function (ea) {
        if (!ea) return [null];
        var updateVersion = function updateVersion(evt, ed, attr) {
          self.interactivelyUpdateProjectDependencyVersion(p, ea, projects, update.curry(false, false, ed.$morph));
        };

        // if (typeof ea === "string") return [["  "], [ea], ["\n"]];
        // if (ea.name) return [["  "], [ea.name + " " + ea.version], ["\n"]]

        if (typeof ea === "string") return [["    "], [ea, { type: actionStyle, onClick: updateVersion }], ["\n"]];
        if (ea.name) return [["    "], [ea.name + " ", { type: localDeps.include(ea) ? localDepStyle : "" }], [ea.version, { type: localDeps.include(ea) ? localDepActionStyle : actionStyle, onClick: updateVersion }], ["\n"]];
        return [null];
      }).compact();

      return printed.concat([[p.name || p.dir.split(/\/|\\/).last()], [" "], p.version ? ["(" + p.version + ")", {
        type: actionStyle,
        onClick: function onClick(evt, ed, attr) {
          self.interactivelyUpdateProjectVersion(p, projects, update.curry(false, false, ed.$morph));
        }
      }] : [""], [" "], ["[open config]", { type: actionStyle, onClick: function onClick(evt, ed, attr) {
          self.project_openConfFile(p);
        } }], ["\n  "]]).concat(printGitStatus(p)).concat([["  dependencies:\n"]]).concat(depsPrinted).concat([["\n"]]);
    }, []);

    thenDo && thenDo(null, printedDeps);
  }

  // -=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-

  function update(fetchGitStatus, shouldDoGitStatus, editor, err) {
    if (err) editor.setStatusMessage(String(err));
    self.projects_showInfo(projects, fetchGitStatus, onlyLocal, shouldDoGitStatus, editor);
  }
}

