function init(aListObject) {
    if (aListObject.title) {
        this.hasTitle = true;
        this.title = aListObject.title;
    } else {
        this.hasTitle = false;
    }
    if (aListObject.items) {
        if (this.hasTitle) {
            if (aListObject.items.indexOf(this.title) != -1) {
                alert("Error: title " + this.title + " should not be an item in the list" +
                " " + this.title + " being treated as an ordinary, selectable item")
                this.hasTitle = false;
                this.setList(aListObject.items)
            } else {
                var items = aListObject.items.slice(0)
                items.pushAt(this.title, 0)
                this.setList(items)
            }
        } else {
            this.setList(aListObject.items)
        }
    } else {
        alert("Error! aListObject must have an item list")
        this.action = function(aSelection) {return;}
    }
    if (aListObject.hasOwnProperty("action")) {
        this.action = aListObject.action
    } else {
        alert("Error! aListObject must have an action")
        this.action = function(aSelection) {return;}
    }

}
