function onDrag(evt) {
    // Compute the value from a new mouse point, and emit it
    var delta = this.localize(evt.getPosition());
    var v = delta.x / (this.innerBounds().width);
    var q = Math.floor(0.5 + v * (this.max - this.min)) / (this.max - this.min);
    this.setScaledValue(Math.max(0, Math.min(1.0, q)));
}

