function setupConnections() {
    this.setSessionId(this.getMorphNamed('SessionIdField').getTextString());
    this.allModules().forEach(function (module) {
        this.moduleConnections.push(
            Global.connect(module, 'handleSaveStatus', this, 'onModuleSaved', {
                updater: function($upd, webR) {
                    webR.status.isSuccess() && $upd(this.sourceObj.module());
                }}));
    }, this);
}

132.source:
function onModuleSaved(module) {
    var selectedModules = this.getMorphNamed('ModuleList').getSelectedItems();
    if (this.sessionId && selectedModules.indexOf(module.name()) >= 0) {
        Global.lively.net.SessionTracker.getSession().sendTo(
            this.sessionId,
            'remoteEvalRequest',
            { expr: 'JSLoader.forcedReload(module("' + module.name() + '").uri())' },
            function(answer) { Global.alertOK(answer.result) });  
    }
}

