function updateLinks() {
// this.updateLinks()
    var rootURL = this.targetURL || URL.source.getDirectory();
    if (rootURL.isLeaf()) rootURL = rootURL.getDirectory();
    var wr = new WebResource(rootURL)
    try {
        wr.getSubElements(0)
    } catch(e) {
        alertOK("could not go to " + rootURL)
        return
    }
    var items = []
    var dirs = (wr.subCollections || []).invoke('getURL').sort()

    if (rootURL.toString() != URL.root.toString()) {
        items.push({
            string: '..',
            value: rootURL.withFilename("../").withRelativePartsResolved()
        })
    }
    items.pushAll(dirs.collect(function(ea) {
        return {
            string: ea.filename(),
            value: ea,
        }
    }))
    var links = (wr.subDocuments || []).invoke('getURL').select(function(ea) {
        return ea.filename().match(/.*\.((xhtml)|(md))/) 
    }).reject(function(ea) {
        return ea.filename().match(/index.xhtml/) || ea.filename().match(/template.xhtml/)
    })
    var links = links.sort()
    // Beware: Context-spefic behavior ;-)
    if (URL.source.toString().match("WriteFirst")) links = links.reverse()

    items.pushAll(links.collect(function(ea) {
        var entry = ea.filename().replace(/\..*/, "")
        if (entry.match(/20[0-9][0-9]-[0-9][0-9]-[0-9][0-9]/g)) {
            entry += " " + new Date(entry).format("ddd")
        }
        new Date()

        return {
            string: entry,
            value: ea,
        }
    }))
    this.get('List').setList(items)
    this.get("URL").textString = rootURL

}

